// Extract inputs from webhook
const intent = $input.item.json.body.intent;
const inputs = $input.item.json.body.inputs || {};
const trace = $input.item.json.body.trace || {};

// Validate intent
if (intent !== 'gmail.triage') {
  return {
    json: {
      status: 'error',
      error: `Invalid intent: ${intent}. Expected gmail.triage`,
      trace
    }
  };
}

// Extract and validate inputs
const query = inputs.query || 'is:unread';
const label = inputs.label;
const dry_run = inputs.dry_run || false;
const max_results = inputs.max_results || 20;

return {
  json: {
    query,
    label,
    dry_run,
    max_results,
    trace,
    request_id: trace.request_id,
    job_id: trace.job_id
  }
};

// Dry run - return preview without making changes
const emails = $input.all();
const trace = $node['Parse Inputs'].json.trace;
const label = $node['Parse Inputs'].json.label;

const preview = emails.slice(0, 5).map(e => ({
  id: e.json.id,
  from: e.json.payload.headers.find(h => h.name === 'From')?.value,
  subject: e.json.payload.headers.find(h => h.name === 'Subject')?.value,
  date: e.json.payload.headers.find(h => h.name === 'Date')?.value
}));

return {
  json: {
    status: 'success',
    result: {
      dry_run: true,
      found: emails.length,
      would_apply_label: label,
      preview: preview,
      note: 'No changes made. Set dry_run=false to apply label.'
    },
    trace
  }
};

// Aggregate results
const labeled = $input.all();
const trace = $node['Parse Inputs'].json.trace;
const query = $node['Parse Inputs'].json.query;
const label = $node['Parse Inputs'].json.label;

return {
  json: {
    status: 'success',
    result: {
      query: query,
      found: labeled.length,
      labeled: labeled.length,
      label_id: label,
      email_ids: labeled.map(e => e.json.id)
    },
    trace
  }
};

// No label provided - just return counts
const emails = $input.all();
const trace = $node['Parse Inputs'].json.trace;
const query = $node['Parse Inputs'].json.query;

const preview = emails.slice(0, 5).map(e => ({
  id: e.json.id,
  from: e.json.payload.headers.find(h => h.name === 'From')?.value,
  subject: e.json.payload.headers.find(h => h.name === 'Subject')?.value
}));

return {
  json: {
    status: 'success',
    result: {
      query: query,
      found: emails.length,
      labeled: 0,
      preview: preview,
      note: 'No label provided. Add label parameter to apply labels.'
    },
    trace
  }
};

// Error handler
const error = $input.item.json.error || 'Unknown error';
const trace = $node['Parse Inputs']?.json?.trace || {};

return {
  json: {
    status: 'error',
    error: error.message || error,
    trace
  }
};
